jQuery(document).ready(function(){
   jQuery("li.woocommerce-MyAccount-navigation-link--dashboard").attr("id","dash-active");
  

  function hasClass(element, cls) {
      return (' ' + element.className + ' ').indexOf(' ' + cls + ' ') > -1;
  }

  var el = document.getElementById('dash-active');
  if(hasClass(el, 'is-active')){
  	jQuery("div.woocommerce-MyAccount-content p").css("display", "none");
  }
  else{
	jQuery("div.woocommerce-MyAccount-content p").css("display", "block");
  }

});


